		// 	if (uneditable) {
		// 		uneditable.toEmpty()
		// 	}
		// })
		this.editor.formatElementStack()
		this.editor.domRender()
		this.editor.collapseToEnd()
		this.editor.rangeRender()
	},
	methods: {
		queryTextStyle() {
			console.log(this.editor.queryTextMark('data-index', '#fff'))
		},
		setTextStyle() {
			this.editor.setTextStyle({
				color: '#87f390'
			})
			this.editor.formatElementStack()
			this.editor.domRender()
			this.editor.rangeRender()
		},
		removeTextStyle() {
